const fs = require('fs');
const { writeExif } = require('./sticker.js');

// FunÃ§Ã£o para converter imagem para WebP
async function imageToWebp(buffer, customMetadata = {}) {
    try {
        // Usa a funÃ§Ã£o existente do sticker.js
        const media = { data: buffer, mimetype: 'image/jpeg' };
        const metadata = { 
            packname: customMetadata.packname || "NEEXT LTDA", 
            author: customMetadata.author || "NEEXT BOT" 
        };
        return await writeExif(media, metadata);
    } catch (error) {
        console.error('Erro ao converter imagem para WebP:', error);
        throw error;
    }
}

// FunÃ§Ã£o para converter vÃ­deo para WebP
async function videoToWebp(buffer, customMetadata = {}) {
    try {
        // Usa a funÃ§Ã£o existente do sticker.js para vÃ­deos
        const media = { data: buffer, mimetype: 'video/mp4' };
        const metadata = { 
            packname: customMetadata.packname || "NEEXT LTDA", 
            author: customMetadata.author || "NEEXT BOT" 
        };
        return await writeExif(media, metadata);
    } catch (error) {
        console.error('Erro ao converter vÃ­deo para WebP:', error);
        throw error;
    }
}

// FunÃ§Ã£o para escrever EXIF em imagens com dados personalizados
async function writeExifImg(buffer, options = {}) {
    try {
        // Marca como rename para usar APENAS valores do usuÃ¡rio
        const packname = options.packname;
        const author = options.author;
        const media = { data: buffer, mimetype: 'image/jpeg' };
        const metadata = { packname, author, _isRename: true };
        return await writeExif(media, metadata);
    } catch (error) {
        console.error('Erro ao escrever EXIF na imagem:', error);
        throw error;
    }
}

// FunÃ§Ã£o para escrever EXIF em vÃ­deos com dados personalizados
async function writeExifVid(buffer, options = {}) {
    try {
        // Marca como rename para usar APENAS valores do usuÃ¡rio
        const packname = options.packname;
        const author = options.author;
        const media = { data: buffer, mimetype: 'video/mp4' };
        const metadata = { packname, author, _isRename: true };
        return await writeExif(media, metadata);
    } catch (error) {
        console.error('Erro ao escrever EXIF no vÃ­deo:', error);
        console.log('ğŸ”„ Tentando processar como imagem estÃ¡tica...');

        // Fallback: tenta processar como imagem se falhar como vÃ­deo
        try {
            const packname = options.packname;
            const author = options.author;
            const media = { data: buffer, mimetype: 'image/webp' };
            const metadata = { packname, author, _isRename: true };
            return await writeExif(media, metadata);
        } catch (fallbackError) {
            console.error('âŒ Fallback tambÃ©m falhou:', fallbackError);
            throw error; // LanÃ§a o erro original
        }
    }
}

module.exports = {
    imageToWebp,
    videoToWebp,
    writeExifImg,
    writeExifVid
};

